/**
 * CampaignRightsTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class CampaignRightsTable extends Doctrine_Table {

  /**
   * Returns an instance of this class.
   *
   * @return CampaignRightsTable
   */
  public static function getInstance() {
    return Doctrine_Core::getTable('CampaignRights');
  }

  /**
   *
   * @param Campaign $campaign
   * @param sfGuardUser $user
   * @return Doctrine_Query
   */
  public function queryByCampaignAndUser(Campaign $campaign, sfGuardUser $user) {
    return $this->createQuery('cr')
        ->where('cr.campaign_id = ? AND cr.user_id = ?', array($campaign->getId(), $user->getId()));
  }

  public function queryByCampaignAndUserAndAdmin(Campaign $campaign, sfGuardUser $user) {
    return $this->queryByCampaignAndUser($campaign, $user)->andWhere('cr.admin = 1');
  }

  /**
   *
   * @param Campaign $campaign
   * @param bool $fetch_user
   * @return Doctrine_Query 
   */
  public function queryByCampaign(Campaign $campaign, $fetch_user = true) {
    $query = $this->createQuery('cr')
      ->where('cr.campaign_id = ?', $campaign->getId());

    if ($fetch_user)
      $query->leftJoin('cr.User u')->addSelect('cr.*, u.*');

    return $query;
  }

  /**
   *
   * @param Campaign $campaign
   * @param bool $fetch_user
   * @return Doctrine_Query 
   */
  public function queryByCampaignActiveMember(Campaign $campaign, $fetch_user = true) {
    return $this->queryByCampaign($campaign, $fetch_user)->andWhere('cr.active = 1 AND cr.member = 1');
  }

  /**
   *
   * @param int $campaign_id
   * @param array $user_ids
   * @return Doctrine_Query
   */
  public function queryByCampaignAndUsers($campaign_id, $user_ids) {
    return $this->createQuery('cr')
        ->where('cr.campaign_id = ?', $campaign_id)
        ->andWhereIn('cr.user_id', $user_ids);
  }

  public function adminIds(sfGuardUser $user) {
    $ids = $this->createQuery('cr')
      ->where('cr.user_id = ? AND cr.admin = 1 AND cr.active = 1', $user->getId())->select('cr.campaign_id')
      ->execute(array(), Doctrine_Core::HYDRATE_SCALAR);
    return array_map('reset', $ids);
  }

  /**
   *
   * @param sfGuardUser $user
   * @return Doctrine_Query
   */
  public function queryByUser(sfGuardUser $user) {
    return $this->createQuery('cr')
        ->where('cr.user_id = ?', $user->getId())
        ->leftJoin('cr.Campaign c')
        ->select('cr.*, c.*');
  }

  /**
   *
   * @param Campaign $campaign
   * @param type $fetch_user
   * @return Doctrine_Query
   */
  public function queryByCampaignAndAdmin(Campaign $campaign, $fetch_user = true) {
    return $this->queryByCampaign($campaign, $fetch_user)->andWhere('cr.active = 1 AND cr.admin = 1');
  }

}