/**
 * OfferTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class OfferTable extends Doctrine_Table {

  /**
   * Returns an instance of this class.
   *
   * @return OfferTable The table instance
   */
  public static function getInstance() {
    return Doctrine_Core::getTable('Offer');
  }

  public function offer(Order $order, $connection = null) {
    $con = $connection ? : $this->getConnection();
    try {
      $con->beginTransaction();


      $offer = new Offer();
      $offer->setUser($order->getUser());
      $offer->setCampaign($order->getQuotas()->getFirst()->getCampaign());

      $order->fillBill($offer);
      
      $offer->save();

      $markup = strtr(UtilMarkdown::transform(strtr(StoreTable::value(StoreTable::BILLING_OFFER), $offer->getSubst1()), false), $offer->getSubst2());
      $offer->setMarkup($markup);
      $offer->save();

      $con->commit();
      return $offer;
    } catch (\Exception $e) {
      $con->rollback();
      return false;
    }
  }

}
