  }

  /**
   *
   * @param int $petition_id
   * @param int $contact_id
   * @param int $hydrationMode
   * @return PetitionContact
   */
  public function findOneByPetitionIdAndContactId($petition_id, $contact_id, $hydrationMode = null) {
    return $this->createQuery('pc')->where('pc.petition_id = ?', $petition_id)->andWhere('pc.contact_id = ?', $contact_id)->fetchOne($hydrationMode);
  }

  public static function salt() {
    $salt = '';
    while (strlen($salt) < 16) {
      $salt .= base_convert(mt_rand(), 10, 36);
    }
    return substr($salt, 0, 16);
  }
  
  public function queryByPetition($petition_id) {
    return $this->createQuery('pc INDEXBY pc.contact_id')->where('pc.petition_id = ?', $petition_id);
  }

